DROP TABLE IF EXISTS `posting`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `posting` (
  `pid` int unsigned NOT NULL AUTO_INCREMENT,
  `ptitle` varchar(30) DEFAULT NULL,
  `pcontent` text,
  `pimage1` varchar(50) DEFAULT NULL,
  `pimage2` varchar(50) DEFAULT NULL,
  `pimage3` varchar(50) DEFAULT NULL,
  `pcategory` varchar(30) DEFAULT NULL,
  `plocation` varchar(100) DEFAULT NULL,
  `pulevel` varchar(45) DEFAULT NULL COMMENT 'user의 등급',
  `plevel` int DEFAULT NULL COMMENT '게시물 - 1 댓글 - 2 대댓글 - 3',
  `pparentid` int DEFAULT NULL COMMENT '댓글일 경우 게시글의 pid , 대댓글의 경우 댓글의 pid',
  `pinitdate` datetime DEFAULT NULL,
  `pupdatedate` datetime DEFAULT NULL,
  `pdeletedate` datetime DEFAULT NULL,
  `user_uid` varchar(20) NOT NULL,
  PRIMARY KEY (`pid`,`user_uid`),
  KEY `fk_posting_user1_idx` (`user_uid`),
  CONSTRAINT `fk_posting_user1` FOREIGN KEY (`user_uid`) REFERENCES `user` (`uid`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;
/*!40101 SET character_set_client = @saved_cs_client */;

